css:
.container {
      width: 100%;
      height: 70vh;
      display: flex;
      flex-direction: column;
     align-items: center;
      justify-content: center;

    }
    .print-format {

        padding-left: 0px;
    }

    .pos-right{
        position:relative;
        right:100px;
    }

    .pos-left{
        position:relative;
        left:100px;
    }

    table {
      width: 60%;
      border-collapse: collapse;
      margin-bottom: 20px;
    }

    .tdimg {
      padding: 10px;
      border: none;  /* Remove cell borders */
      text-align: center;  /* Align text horizontally in the center */
      vertical-align: middle;  /* Align text vertically in the middle */
    }

    .tdarabic {
      padding: 10px;
      border: none;  /* Remove cell borders */
      text-align: right;  /* Align text horizontally in the center */
        /* Align text vertically in the middle */
    }

    img {
      display: block;
      max-width: 20%;
      height: auto;
      margin: 0 auto;
    }

    hr {
      width: 100%;
      margin-bottom: 20px;
    }
    .font {
      font-size: x-large;
    }
    .largefont{
      font-size: large;
    }
    .title-td{
      font-size: x-large;
      text-align: center;
      font-weight: bold;
       vertical-align: middle; 
    }
     .thank-td{
      font-size: large;
      text-align: center;
      font-weight: bold;
       vertical-align: middle; 
    }
    .dep-td{
      font-size: large;
      text-align: left;
      font-weight: bold;
       vertical-align: middle; 
    }
    .resized-td {
    width: 200px;
    height: 100px;
  }

html:
{% if doc.transaction_scope == "Among Companies" %}



   <div class="container">
    <table>
          {% set date_parts = doc.start_date.split() %}
{% set day = date_parts[0] %}
{% set month = date_parts[1] %}
{% set year = date_parts[2].rstrip(',') %}
      <tr>
        <td class="pos-right resized-td">Republic of Yemen <br>
          Sana’a University  <br>
          <!--{{ doc.company }}-->
          {{ doc.start_with_company }}                    <br>   
          DATE: {{ day }} {{ month }} {{ year }}  <br>
        </td>
{% if not (doc.transaction_scope == "With External Entity" and doc.type == "Incoming") %}
{% set logo=frappe.db.get_value("Company", {"company_name": doc.start_with_company}, "company_logo")%}
{% else %}
    {% set logo = frappe.db.get_value("External Entity", {"external_entity": doc.main_external_entity_from}, "logo") %}

{% endif %}
          <!--{% set company_logo=frappe.db.get_value("Company", {"company_name": doc.start_with_company}, "company_logo")%}-->

        <td  class="tdimg  resized-td">
          <img src="{{logo}}" alt="Logo">
        </td>
        <td  class="tdarabic resized-td pos-left">الجمهورية اليمنية<br>
          جامعة صنعاء<br>
          {{_(doc.start_with_company)}} <br>


       <!--{{doc.hijri_date}}   :التاريخ-->
        </td>
      </tr>
    </table>

    <hr>

    <table>

      <tr>

        <td class="font pos-right"> المحترم</td>
        <td class="tdarabic font pos-left">
            <span>
                {% if doc.transaction_scope != "With External Entity" and doc.recipients %}
                  {% set first_recipient = doc.recipients[0].recipient_name %}
            الأخ /
                  {{ first_recipient }}

            {% elif doc.transaction_scope == "With External Entity" and doc.type == "Outgoing" %}
                  {% set first_recipient = doc.external_entity_designation_to %}

            {{ first_recipient }}
            {% endif %}
            </span> 
        </td>
      </tr>
    </table>
    <br>
    <table>
      <tr>
<td class="title-td">الموضوع / {{ doc.title }}</td>
</table>
    <br><br> 
    <table>
      <tr>
        <td class="tdarabic largefont"> {{ doc.transaction_description }}        </td>
      </tr>
    </table>
    <br><br>
    <table>
      <tr>
        <td class="thank-td">..شاكرين تعاونكم معنا </td>
      </tr>
    </table>
    <br><br><br><br><br><br>
    <table>


    {% for signature in doc.signatories %}
    {% if signature.official %}

        <tr>
            <td class="dep-td">: {{ signature.signatory_designation }} <br> <br>
            </td>
        </tr>
        {% endif %}
    {% endfor %}
    {% for signature in doc.signatories %}
    {% if signature.official %}
        <tr>
            <td> {{ signature.signatory_name|join }}</td>
        </tr>
        {% endif %}
    {% endfor %}









{% endif %}
